declare module '*.less' {
  const less: any;
  export = less;
}

declare module 'require*' {
  const require: any;
  export = require;
}

declare module '*.jpeg' {
  const jpeg: any;
  export = jpeg;
}

declare module '*.gif' {
  const gif: any;
  export = gif;
}

declare module '*.tiff' {
  const tiff: any;
  export = tiff;
}

declare module '*.svg' {
  const svg: any;
  export = svg;
}